if(msg.payload.search("d")===-1){
    //This search allows us to avoid malformed messagges
    return null;
}
//Clearing the message 
msg.payload = msg.payload.substring(msg.payload.search("d") +1,msg.payload.length);
msg.date = 0;
return msg;

//Get the info the mote with a given ID. If no mote is present, set rec_ID to 0
var rec_ID=context.get("user_ID" + msg.user + "rec_ID" + msg.payload) || 0;
//Minutes of delay before send another notification
var delayBeforeNotification = 10;
//If the mote is present, check its timestamp
if (rec_ID !== 0) {
    var deltaHours = parseInt(msg.date.substring(msg.date.search("T") + 1, msg.date.search("T") + 3)) - parseInt(rec_ID.substring(rec_ID.search(",")+1,rec_ID.search(",")+3));
    var deltaMin = deltaHours*60 + parseInt(msg.date.substring(msg.date.search("T") + 4, msg.date.search("T") + 6)) - parseInt(rec_ID.substring(rec_ID.search(",")+4,rec_ID.search(",")+6));
    var deltaSec = deltaMin*60 + parseInt(msg.date.substring(msg.date.search("T") + 7, msg.date.search("T") + 9)) - parseInt(rec_ID.substring(rec_ID.search(",")+7,rec_ID.search(",")+9));
       //If 10 minutes ore more have passed since last notification, update rec_ID and send a notification
    if (deltaSec >= delayBeforeNotification*60) {
        context.set("user_ID" + msg.user + "rec_ID" + msg.payload, msg.payload + "," + msg.date.substring(msg.date.search("T") + 1, msg.date.search("Z")));
        return msg;
    } else {
        //if not enought time is passed, set payload to -1.
        msg.payload = -1;
        return msg;
    }
} else {
    //If it's the first time this ID compares, set the timestamp and send a notification
    context.set("user_ID" + msg.user + "rec_ID" + msg.payload, msg.payload + "," + msg.date.substring(msg.date.search("T") + 1, msg.date.search("Z")));
    return msg;
}

